Method.Implementation:
kill @..#Source set @..#Source=..#Folder_$zconvert($znspace,"l")_"/" set @..#Source@("CLS","*")="cls/" set @..#Source@("DFI","*")="dfi/"
set SourceControl=..%New("") set sc=$$$OK
&sql(declare SU1 cursor for select ID into :class from %Dictionary.ClassDefinition where (NOT (ID %STARTSWITH '%')) AND NOT (ID %STARTSWITH 'Util.SourceControl') AND NOT (ID %STARTSWITH 'Ens') AND NOT (ID %STARTSWITH 'CSPX') AND NOT (ID %STARTSWITH 'INFORMATION.SCHEMA') ) &sql(open SU1) for { &sql(fetch SU1) quit:SQLCODE set sc=SourceControl.OnAfterSave(class_".CLS") quit:'sc }
&sql(declare SU2 cursor for select documentname into :name from %DeepSee_UserLibrary.FolderItem) &sql(open SU2) for { &sql(fetch SU2) quit:SQLCODE set sc=SourceControl.OnAfterSave(name) quit:'sc }
quit sc

/// if file is dfi, make folder-name.type.dfi to folder.name-type.dfi
if $zconvert($piece(InternalName,".",*),"U")="DFI" {
	s InternalName=$p(InternalName,"-")_"."_$p($p(InternalName,"-",2),".",1)_"-"_$p(InternalName,".",2)_".DFI"
	}

Set name=..Name(InternalName)
// if cache-tort-git mode then introduce type in the file name
if ..#ExpMode set $p(name,".",*)=($zconvert($piece(InternalName,".",*),"l")_"."_$p(name,".",*))
Quit $select(name="":"",1:^Sources_$translate(name,"\","/"))
